import React, { Component } from 'react'
import './index.css'

export default class Footer extends Component {

    getCompleteTodoCnt = (todos) => {
        const newTodos = todos.filter((todoObj)=>{
            return todoObj.done == true
        })
        return newTodos.length
    }

    removeCompleteTodo = (todos)

    render() {
        const {todos} = this.props

        return (
            <div className="todo-footer">
                <label>
                <input type="checkbox"/>
                </label>
                <span><span>已完成 {this.getCompleteTodoCnt(todos)}</span> / 全部 {todos.length}</span>
                <button onclick={this.removeCompleteTodo} className="btn btn-danger">清除已完成任务</button>
            </div>
        )
    }
}
